        // } else {
            return (
                <div className="tc">
                    <h1 className='f1'>RobotsFriend</h1>
                    <SearchBox searchChange={this.onSearchChange}/>
                    <CardList robots ={filterRobots}/>
                </div>
            );
        }

    }
// }

export default App;
